jkmy');		$time = time();		$data = array(			't' => $time,			'toaddress' => $myzc['username'],			'amount' => $myzc['num'],		);		$sign = getSign($secret, $data);		$data2 = array(			't' => $time,			'toaddress' => $myzc['username'],			'amount' => $myzc['num'],			'sign' => $sign,		);				$url.= '/sendToAddress';				$re  =  json_decode(request_post($url, $data2),1);				if (isset($re['err'])) {			$this->error($re['err']);		}				if ($re['result'] == 'ok') {		   $rs[] = M('Myzc')->where(array('id' => trim($id)))->save(array('txid' => $re['txid']));		} 					if (check_arr($rs)) {			$mo->execute('commit');			$mo->execute('unlock tables');		   $this->success('转账成功！');		}		else {			$mo->execute('rollback');			$mo->execute('unlock tables');            $this->error('转账失败！');		}	}	public function checkUpdata()	{		if (!S(MODULE_NAME . CONTROLLER_NAME . 'checkUpdata')) {			$tables = M()->query('show tables');			$tableMap = array();			foreach ($tables as $table) {				$tableMap[reset($table)] = 1;			}			if (!isset($tableMap['cy_finance'])) {				M()->execute("\r\n" . '                   CREATE TABLE `cy_finance` (' . "\r\n\t" . '`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT \'自增id\',' . "\r\n\t" . '`userid` INT(11) UNSIGNED NOT NULL COMMENT \'用户id\',' . "\r\n\t" . '`coinname` VARCHAR(50) NOT NULL COMMENT \'币种\',' . "\r\n\t" . '`num_a` DECIMAL(20,8) UNSIGNED NOT NULL COMMENT \'之前正常\',' . "\r\n\t" . '`num_b` DECIMAL(20,8) UNSIGNED NOT NULL COMMENT \'之前冻结\',' . "\r\n\t" . '`num` DECIMAL(20,8) UNSIGNED NOT NULL COMMENT \'之前总计\',' . "\r\n\t" . '`fee` DECIMAL(20,8) UNSIGNED NOT NULL COMMENT \'操作数量\',' . "\r\n\t" . '`type` VARCHAR(50) NOT NULL COMMENT \'操作类型\',' . "\r\n\t" . '`name` VARCHAR(50) NOT NULL COMMENT \'操作名称\',' . "\r\n\t" . '`nameid` INT(11) NOT NULL COMMENT \'操作详细\',' . "\r\n\t" . '`remark` VARCHAR(50) NOT NULL COMMENT \'操作备注\',' . "\r\n\t" . '`mum_a` DECIMAL(20,8) UNSIGNED NOT NULL COMMENT \'剩余正常\',' . "\